import React, { Component } from 'react'
import { detailNotes } from './data'
import uuid from 'uuid'


const NoteContext = React.createContext();


class NoteProvider extends Component {
    state = {
        notes : [],
        detailNotes : detailNotes,
        note : detailNotes,
        editItem : false

    }



    handleChange = (e) => {
        this.setState({
            note : e.target.value
        })
    }

    handleDelete = (e) => {
        console.log('note deleted')
    }

    handleSubmit = (e) => {


      const newNote = {
          id : this.state.id,
          note : this.state.item,
      }  
      console.log(newNote)

      const updatedNotes = [...this.state.notes,newNote]
         this.setState({
             notes : updatedNotes,
             note : '',
             id : uuid(),
             editNote : false
         })
     }
    render() {
        return (
            <NoteContext.Provider value={{
                title : this.state.title,
                info : this.state.info,
                date : this.state.date,
                handleEdit : this.handleChange,
                handleSubmit : this.handleSubmit,
                handleDelete : this.handleDelete,
            }}>
                {this.props.children}
            </NoteContext.Provider>
        )
    }
}


const NoteConsumer = NoteContext.Consumer

export { NoteProvider , NoteConsumer };
